import { colors } from "@mui/material";

const getDesignTokens = mode => ({
  palette: {
    mode, 
    ...(mode === "light" ? {
      secondary: {
        main: colors.green[100]
      }  
    } : {
      secondary: {
        main: colors.green[900]
      }
    })
  }
});

