import { colors } from "@mui/material";

const getDesignTokens = (mode) => ({
  palette: {
    mode,
    ...(mode === "light"
      ? {
          primary: {
            main: colors.green[700],
            light: colors.green[600],
            dark: colors.green[900],
          },
        }
      : {
          primary: {
            main: colors.green[800],
            light: colors.green[700],
            dark: colors.green[900],
          },
        }),
  },
});

export default getDesignTokens;
